mapper.select:

	select a.subject, a.artist_id, a.file_path, a.image_path, a.board_num 
	from uploadboard a, storage_list b 
	where a.board_num = b.board_num and b.artist_id=#{artist_id}


	select a.subject, a.artist_id, a.file_path, a.image_path, a.board_num 
	from museboard a, storage_list b 
	where a.board_num = b.board_num and b.artist_id=#{artist_id}
